import{_ as e,c as o,o as r,a as c}from"./app.7001bd85.js";const v='{"title":"MMO Server From Scratch(3) - Scene Server(0) - Introduction","description":"","frontmatter":{"title":"MMO Server From Scratch(3) - Scene Server(0) - Introduction","date":"2022-10-19T03:42:13.000Z","author":"dyzdyz010","tags":["game","mmo","server","elixir"],"collection":"MMO Server From Scratch","draft":false},"headers":[{"level":2,"title":"\u529F\u80FD\u89E3\u6790","slug":"\u529F\u80FD\u89E3\u6790"},{"level":3,"title":"\u79FB\u52A8\u540C\u6B65","slug":"\u79FB\u52A8\u540C\u6B65"},{"level":3,"title":"\u73A9\u5BB6\u8FDB\u5165/\u79BB\u5F00\u573A\u666F","slug":"\u73A9\u5BB6\u8FDB\u5165-\u79BB\u5F00\u573A\u666F"},{"level":2,"title":"\u7B80\u8981\u8BBE\u8BA1","slug":"\u7B80\u8981\u8BBE\u8BA1"},{"level":3,"title":"\u8BA1\u7B97\u91CF\u89C4\u5212","slug":"\u8BA1\u7B97\u91CF\u89C4\u5212"},{"level":3,"title":"\u521D\u6B65\u76D1\u7763\u6811","slug":"\u521D\u6B65\u76D1\u7763\u6811"},{"level":2,"title":"\u63A5\u4E0B\u6765\u7684\u5DE5\u4F5C","slug":"\u63A5\u4E0B\u6765\u7684\u5DE5\u4F5C"}],"relativePath":"posts/mmo-server-from-scratch/2022/20221016-mmo-server-from-scratch(3)-scene-server-aoi.md"}',d={},a=c('<p>\u4ECA\u5929\u5F00\u59CB\u5B9E\u73B0\u670D\u52A1\u5668\u7684\u7B2C\u4E09\u4E2A\u90E8\u4EF6 - <strong>scene_server</strong>\uFF0C\u5373\u573A\u666F\u670D\u52A1\u5668\u3002\u672C\u8282\u53EA\u5173\u6CE8\u63A5\u4E0B\u6765\u4E00\u6BB5\u65F6\u95F4\u5C06\u8981\u7B80\u5355\u5B9E\u73B0\u7684\u5185\u5BB9\uFF0C\u800C\u4E0D\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u573A\u666F\u670D\u52A1\u5668\u3002\u573A\u666F\u670D\u52A1\u5668\u5305\u542B\u7684\u5185\u5BB9\u6781\u591A\uFF0C\u6211\u4EEC\u5C06\u4E00\u6B65\u4E00\u6B65\u5C06\u5176\u5B9E\u73B0\u3002</p><h2 id="\u529F\u80FD\u89E3\u6790" tabindex="-1">\u529F\u80FD\u89E3\u6790 <a class="header-anchor" href="#\u529F\u80FD\u89E3\u6790" aria-hidden="true">#</a></h2><p>\u573A\u666F\u670D\u52A1\u5668 <code>scene_server</code> \u7528\u4E8E\u4E3A\u73A9\u5BB6\u63D0\u4F9B\u573A\u666F\u670D\u52A1\uFF0C\u5373\u73A9\u5BB6\u5728\u8FDB\u5165\u5730\u56FE\u4E4B\u540E\u7684\u4E00\u5207\u4E0E\u573A\u666F\u76F8\u5173\u7684\u5185\u5BB9\u5747\u7531\u573A\u666F\u670D\u52A1\u5668\u63D0\u4F9B\uFF0C\u5982\u79FB\u52A8\u540C\u6B65\u3001\u5468\u56F4\u73A9\u5BB6\u72B6\u6001\u66F4\u65B0\u3001\u6218\u6597\u7ED3\u7B97\u3001\u73A9\u5BB6\u884C\u4E3A\u9A8C\u8BC1\u7B49\u3002</p><p><code>scene_server</code> \u7684\u6D88\u606F\u53EF\u4EE5\u6765\u81EA\u5176\u4ED6\u4E0D\u540C\u670D\u52A1\u5668\uFF0C\u5982\u7F51\u5173\u670D\u52A1\u5668 <code>gate_server</code>\u3001\u4E16\u754C\u670D\u52A1\u5668 <code>world_server</code>\u3001\u7528\u6237\u4EE3\u7406\u670D\u52A1\u5668 <code>agent_server</code>\u7B49\u3002\u4F46\u662F\u5982\u679C\u628A\u5168\u90E8\u7684\u529F\u80FD\u70B9\u4E00\u6B21\u6027\u5168\u90E8\u7EB3\u5165\u8003\u8651\u7684\u8BDD\u6211\u6015\u4F1A\u987E\u4E0D\u8FC7\u6765\uFF0C\u6240\u4EE5\u76EE\u524D\u8BA9\u6211\u4EEC\u628A\u6CE8\u610F\u529B\u96C6\u4E2D\u5728\u573A\u666F\u670D\u52A1\u5668\u6700\u57FA\u672C\u7684\u804C\u80FD\u4E0A\uFF1A<strong>\u79FB\u52A8\u540C\u6B65</strong>\u3002</p><p>\u5F53\u7136\u6211\u4EEC\u4E0D\u80FD\u4E22\u5230\u6700\u57FA\u7840\u7684\u529F\u80FD\uFF1A<code>\u73A9\u5BB6\u8FDB\u5165/\u79BB\u5F00\u573A\u666F</code></p><h3 id="\u79FB\u52A8\u540C\u6B65" tabindex="-1">\u79FB\u52A8\u540C\u6B65 <a class="header-anchor" href="#\u79FB\u52A8\u540C\u6B65" aria-hidden="true">#</a></h3><p>\u4EC0\u4E48\u662F\u79FB\u52A8\u540C\u6B65\uFF1F\u7B80\u5355\u5730\u8BF4\uFF0C\u5728\u591A\u4EBA\u6E38\u620F\u91CC\uFF0C\u4F60\u9700\u8981\u770B\u5230\u4F60\u8EAB\u8FB9\u7684\u5176\u4ED6\u73A9\u5BB6\uFF0C\u961F\u53CB\u4E5F\u597D\u3001\u654C\u4EBA\u4E5F\u597D\uFF0C\u4F60\u5E0C\u671B\u80FD\u591F\u770B\u5230\u8EAB\u8FB9\u5176\u4ED6\u73A9\u5BB6\u7684\u5404\u79CD\u64CD\u4F5C\uFF0C\u89D2\u8272\u7684\u4F4D\u7F6E\u548C\u79FB\u52A8\u4E5F\u662F\u5404\u79CD\u64CD\u4F5C\u4E2D\u7684\u4E00\u79CD\u3002\u4E3A\u4E86\u5B9E\u73B0\u8FD9\u79CD\u6548\u679C\uFF0C\u5F53\u4F60\u7684\u89D2\u8272\u5728\u79FB\u52A8\u65F6\uFF0C\u4F60\u9700\u8981\u5C06\u81EA\u5DF1\u79FB\u52A8\u7684\u4FE1\u606F\u53D1\u9001\u7ED9\u8EAB\u8FB9\u6240\u6709\u73A9\u5BB6\uFF0C\u4EE5\u4FBF\u4ED6\u4EEC\u5728\u81EA\u5DF1\u7684\u5BA2\u6237\u7AEF\u4E0A\u66F4\u65B0\u4F60\u7684\u89D2\u8272\u8868\u73B0\uFF0C\u4F7F\u5F97\u4ED6\u4EEC\u770B\u5230\u7684\u548C\u4F60\u770B\u5230\u7684\u5185\u5BB9\u662F <strong>\u4E00\u81F4</strong> \u7684\u3002</p><p>\u8FD9\u91CC\u6D89\u53CA\u4E24\u4E2A\u6761\u4EF6\uFF1A</p><ol><li>\u5C06\u81EA\u5DF1\u7684\u4FE1\u606F\u53D1\u9001\u7ED9\u5176\u4ED6\u591A\u4E2A\u73A9\u5BB6</li><li>\u4FE1\u606F\u53D1\u9001\u7684\u76EE\u6807\u662F\u81EA\u5DF1\u5468\u56F4\u4E00\u5B9A\u8303\u56F4\u5185</li></ol><p>\u5373\uFF1A\u5728\u6240\u6709\u73A9\u5BB6\u4E2D\u641C\u7D22\u51FA\u81EA\u5DF1\u8EAB\u8FB9\u7684\u73A9\u5BB6\uFF0C\u5E76\u5411\u4ED6\u4EEC\u5E7F\u64AD\u81EA\u5DF1\u7684\u79FB\u52A8\u6D88\u606F\u3002\u8FD9\u4E2A\u529F\u80FD\u6709\u4E2A\u4E13\u6709\u540D\u8BCD\uFF0C\u53EB\u505A <code>AOI(Area Of Interest)</code>\u3002\u987E\u540D\u601D\u4E49\uFF0C\u6BCF\u4E2A\u73A9\u5BB6\u90FD\u5177\u6709\u4E00\u4E2A\u81EA\u5DF1\u611F\u5174\u8DA3\u7684\u8303\u56F4\uFF0C\u5728\u8FD9\u4E2A\u8303\u56F4\u5185\u53D1\u751F\u7684\u4E8B\u60C5\u624D\u4F1A\u544A\u8BC9\u5BA2\u6237\u7AEF\uFF0C\u8D85\u51FA\u8FD9\u4E2A\u8303\u56F4\u7684\u52A8\u9759\u4E00\u6982\u4E0D\u7BA1\uFF08\u5F53\u7136\u4E5F\u6CA1\u6709\u8FD9\u4E48\u7EDD\u5BF9\uFF09\u3002</p><p>\u56E0\u6B64\u4E3A\u4E86\u5B9E\u73B0\u5728\u6E38\u620F\u4E2D\u80FD\u591F\u770B\u5230\u5176\u4ED6\u73A9\u5BB6\u7684\u79FB\u52A8\uFF0C\u6211\u4EEC\u9700\u8981\u5B9E\u73B0\u4E00\u4E2A <code>AOI</code> \u7CFB\u7EDF\u3002\u7ECF\u8FC7\u4E00\u756A\u8D44\u6599\u641C\u7D22\uFF0C\u76EE\u524D\u5B9E\u73B0 <code>AOI</code> \u7684\u65B9\u5F0F\u4E3B\u8981\u6709\u4E09\u79CD\uFF1A</p><ol><li>\u706F\u5854</li><li>\u4E5D\u5BAB\u683C</li><li>\u5341\u5B57\u94FE\u8868</li></ol><p>\u8FD9\u4E09\u79CD\u65B9\u5F0F\u5927\u5BB6\u53EF\u4EE5\u81EA\u884C\u641C\u7D22\u3002\u5728\u8FD9\u91CC\u6211\u9009\u62E9\u4E86 <code>\u5341\u5B57\u94FE\u8868</code> \u4F5C\u4E3A\u524D\u671F\u7C97\u7565\u5B9E\u73B0\uFF0C\u4E5F\u8BA9\u81EA\u5DF1\u5BF9\u76F8\u5173\u6982\u5FF5\u8FDB\u884C\u4E86\u89E3\u3002</p><h3 id="\u73A9\u5BB6\u8FDB\u5165-\u79BB\u5F00\u573A\u666F" tabindex="-1">\u73A9\u5BB6\u8FDB\u5165/\u79BB\u5F00\u573A\u666F <a class="header-anchor" href="#\u73A9\u5BB6\u8FDB\u5165-\u79BB\u5F00\u573A\u666F" aria-hidden="true">#</a></h3><p>\u73A9\u5BB6\u8FDB\u5165\u6216\u79BB\u5F00\u573A\u666F\u7684\u60C5\u51B5\u4E0D\u6B62\u4E00\u79CD\uFF0C\u76EE\u524D\u6211\u80FD\u60F3\u5230\u4EE5\u4E0B\u51E0\u79CD\uFF1A</p><ol><li>\u73A9\u5BB6\u8FDB\u5165/\u79BB\u5F00\u6E38\u620F</li><li>\u73A9\u5BB6\u79FB\u52A8\u8FC7\u7A0B\u4E2D\u53BB\u5F80\u5176\u4ED6\u573A\u666F\u670D\u52A1\u5668</li><li>\u73A9\u5BB6\u56E0\u4E3A\u67D0\u4E9B\u73A9\u6CD5\uFF08\u5982\u526F\u672C\u3001\u4F20\u9001\u7B49\uFF09\u79BB\u5F00/\u8FDB\u5165\u5927\u4E16\u754C</li></ol><p>\u5F53\u524D\u4E3A\u4E86\u65B9\u4FBF\u6211\u4EEC\u53EA\u8003\u8651\u7B2C\u4E00\u79CD\uFF0C\u5176\u4ED6\u4E24\u79CD\u5F85\u540E\u671F\u4EE3\u7801\u5B8C\u5584\u4E4B\u540E\u518D\u8FDB\u884C\u5B9E\u73B0</p><h2 id="\u7B80\u8981\u8BBE\u8BA1" tabindex="-1">\u7B80\u8981\u8BBE\u8BA1 <a class="header-anchor" href="#\u7B80\u8981\u8BBE\u8BA1" aria-hidden="true">#</a></h2><h3 id="\u8BA1\u7B97\u91CF\u89C4\u5212" tabindex="-1">\u8BA1\u7B97\u91CF\u89C4\u5212 <a class="header-anchor" href="#\u8BA1\u7B97\u91CF\u89C4\u5212" aria-hidden="true">#</a></h3><p><code>scene_server</code> \u4F5C\u4E3A\u573A\u666F\u7BA1\u7406\u670D\u52A1\u5668\uFF0C\u96C6\u591A\u9879\u804C\u80FD\u4E8E\u4E00\u8EAB\uFF0C\u4E14\u591A\u6570\u804C\u80FD\u5C5E\u4E8E\u8BA1\u7B97\u5BC6\u96C6\u578B\u3002<code>Elixir</code> \u53EF\u80FD\u518D\u5E76\u53D1\u65B9\u9762\u6BD4\u8F83\u64C5\u957F\uFF0C\u4F46\u5728\u6570\u5B66\u8BA1\u7B97\u65B9\u9762\u4E0E <code>C/C++</code> \u4E00\u7CFB\u7684\u8BED\u8A00\u76F8\u6BD4\u5C31\u5DEE\u5F97\u591A\u4E86\u3002\u4F46\u5E78\u8FD0\u7684\u662F\uFF0C<code>Elixir</code> \u548C <code>Erlang</code> \u4E00\u6837\u53EF\u4EE5\u5141\u8BB8\u901A\u8FC7\u591A\u79CD\u65B9\u5F0F\u4E0E\u5916\u90E8\u7A0B\u5E8F\u901A\u4FE1\uFF1A</p><ol><li>C Node</li><li>NIF</li><li>Port Driver</li><li>Port</li></ol><p>\u6BCF\u79CD\u65B9\u6CD5\u90FD\u6709\u4E0D\u540C\u7684\u7279\u70B9\u548C\u9002\u7528\u73AF\u5883\u3002\u5728\u8FD9\u4E2A\u9879\u76EE\u91CC\uFF0C\u6211\u4EEC\u9700\u8981\u7684\u662F\u5DE8\u5927\u7684\u8BA1\u7B97\u91CF\u548C\u76F8\u5BF9\u8F83\u4F4E\u7684\u5EF6\u8FDF\uFF0C\u800C\u5728\u4EE5\u4E0A\u56DB\u79CD\u65B9\u6CD5\u4E2D\uFF0C<code>NIF</code> \u7684\u6D88\u606F\u4F20\u9012\u5EF6\u8FDF\u662F\u6700\u4F4E\u7684\uFF0C\u800C\u4E14\u6709\u4E00\u4E2A\u975E\u5E38\u68D2\u7684 <code>NIF</code> \u5E93\u4F7F\u5F97 <code>NIF</code> \u7684\u5F00\u53D1\u8FC7\u7A0B\u66F4\u52A0\u9AD8\u6548\uFF0C\u7A0B\u5E8F\u66F4\u52A0\u5065\u58EE\u2014\u2014 <code>Rustler</code>\u3002\u8BE5\u5E93\u4F7F\u7528 <code>Rust</code> \u4F5C\u4E3A <code>NIF</code> \u51FD\u6570\u7684\u5F00\u53D1\u8BED\u8A00\uFF0C\u517C\u5177\u4E86\u5185\u5B58\u5B89\u5168\u548C\u9AD8\u6548\uFF0C\u53EF\u8C13\u5B8C\u7F8E\u4E4B\u9009\u3002</p><p>\u56E0\u6B64\uFF0C\u5C06\u91CD\u5EA6\u8BA1\u7B97\u7684\u5185\u5BB9\u6254\u7ED9 <code>Rust</code> \u6765\u505A\uFF0C\u5982 <code>AOI</code> \u6570\u636E\u7BA1\u7406\u3001\u5404\u79CD\u8BA1\u7B97\u9A8C\u8BC1\u7B49\uFF1B\u5E76\u53D1\u6D88\u606F\u4F20\u9012\u7684\u5DE5\u4F5C\u7531 <code>Elixir</code> \u6765\u5B8C\u6210\uFF0C\u5982\u73A9\u5BB6\u4E4B\u95F4\u7684\u4EA4\u4E92\u3001\u6D88\u606F\u5E7F\u64AD\u7B49\u3002</p><h3 id="\u521D\u6B65\u76D1\u7763\u6811" tabindex="-1">\u521D\u6B65\u76D1\u7763\u6811 <a class="header-anchor" href="#\u521D\u6B65\u76D1\u7763\u6811" aria-hidden="true">#</a></h3><p>\u4E3A\u4E86\u8BA9\u524D\u671F\u7684\u4EE3\u7801\u5C3D\u91CF\u7B80\u5316\uFF0C\u524D\u671F\u7684\u529F\u80FD\u89C4\u5212\u76EE\u524D\u53EA\u5305\u62EC\u4EE5\u4E0B\u51E0\u90E8\u5206\uFF1A</p><ol><li>\u73A9\u5BB6\u8FDB\u7A0B\u6A21\u5757 <code>PlayerCharacter</code>\uFF0C\u6BCF\u4E2A\u73A9\u5BB6\u4E3A\u4E00\u4E2A\u8FDB\u7A0B\uFF0C\u73A9\u5BB6\u4EA4\u4E92\u901A\u8FC7\u8FDB\u7A0B\u95F4\u6D88\u606F\u4F20\u9012\u5B9E\u73B0</li><li>\u73A9\u5BB6\u7BA1\u7406\u6A21\u5757 <code>PlayerManager</code>\uFF0C\u7528\u6765\u7BA1\u7406\u73A9\u5BB6 <code>ID</code> \u4E0E\u8FDB\u7A0B\u4E4B\u95F4\u7684\u6620\u5C04\uFF0C\u65B9\u4FBF\u5176\u4ED6\u8FDB\u7A0B\u8FDB\u884C\u67E5\u8BE2\u4ECE\u800C\u5411\u76EE\u6807\u73A9\u5BB6\u8FDB\u7A0B\u53D1\u9001\u6D88\u606F</li><li><code>AOI</code> \u7BA1\u7406\u6A21\u5757 <code>Aoi</code>\uFF0C\u7528\u6765\u4E0E <code>NIF</code> \u901A\u4FE1\uFF0C\u64CD\u7EB5 <code>AOI</code> \u6570\u636E\u7ED3\u6784\uFF08\u5341\u5B57\u94FE\u8868\uFF09</li><li>\u96C6\u7FA4\u64CD\u4F5C\u6A21\u5757 <code>Interface</code>\uFF0C\u4E0E\u5176\u4ED6\u670D\u52A1\u5668\u4E00\u6837</li></ol><p>\u76D1\u7763\u6811\u5F62\u6001\u5927\u6982\u5982\u4E0B\uFF1A</p><p>\u5176\u4E2D\uFF1A</p><ul><li>application - <code>scene_server</code>\u4E3B\u7A0B\u5E8F</li><li>InterfaceSup - <code>Interface</code>\u6A21\u5757\u76D1\u7763\u8005\u8FDB\u7A0B</li><li>AoiSup - <code>Aoi</code>\u7BA1\u7406\u6A21\u5757\u76D1\u7763\u8005\u8FDB\u7A0B</li><li>PlayerManagerSup - \u73A9\u5BB6\u7BA1\u7406<code>PlayerManager</code>\u6A21\u5757\u76D1\u7763\u8005\u8FDB\u7A0B</li><li>PlayerCharacterSup - \u73A9\u5BB6\u8FDB\u7A0B<code>PlayerCharacter</code>\u6A21\u5757\u76D1\u7763\u8005\u8FDB\u7A0B</li><li>Aoi - <code>AOI</code>\u7BA1\u7406\u6A21\u5757\u8FDB\u7A0B</li><li>PlayerManager - \u73A9\u5BB6\u7BA1\u7406\u6A21\u5757\u8FDB\u7A0B</li><li>PlayerCharacter - \u73A9\u5BB6\u8FDB\u7A0B</li></ul><h2 id="\u63A5\u4E0B\u6765\u7684\u5DE5\u4F5C" tabindex="-1">\u63A5\u4E0B\u6765\u7684\u5DE5\u4F5C <a class="header-anchor" href="#\u63A5\u4E0B\u6765\u7684\u5DE5\u4F5C" aria-hidden="true">#</a></h2><p>\u524D\u671F\u4E00\u4E2A\u6781\u81F4\u7B80\u5355\u7684 <code>scene_server</code> \u5C31\u5148\u89C4\u5212\u5230\u8FD9\u91CC\uFF0C\u672A\u6765\u8FD8\u4F1A\u589E\u52A0\u5176\u4ED6\u66F4\u591A\u529F\u80FD\u3002\u4E0B\u4E00\u7BC7\u5C06\u7740\u91CD\u5B9E\u73B0 <code>Aoi</code> \u7684 <code>NIF</code> \u90E8\u5206\uFF0C\u4F7F\u7528 <code>Rust</code> \u7F16\u5199\u3002\u656C\u8BF7\u671F\u5F85\u3002</p>',31),l=[a];function i(t,s,n,h,p,_){return r(),o("div",null,l)}var g=e(d,[["render",i]]);export{v as __pageData,g as default};
