import{_ as l,c as i,o as e,a}from"./app.7001bd85.js";const f='{"title":"\u673A\u5668\u5B66\u4E60\u8BFE\u7A0B\u7ED3\u675F\u603B\u7ED3","description":"","frontmatter":{"title":"\u673A\u5668\u5B66\u4E60\u8BFE\u7A0B\u7ED3\u675F\u603B\u7ED3","date":"2017-07-19T13:13:59.000Z","tags":["code","review","ml"],"author":"dyzdyz010","collection":"study"},"headers":[],"relativePath":"posts/study/2017/ml-course-summary-review.md"}',t={},o=a("<p>\u4E0D\u77E5\u4E0D\u89C9\uFF0C\u6211\u5728Coursera\u4E0A\u8DDF\u968FAndrew\u5927\u795E\u7684\u673A\u5668\u5B66\u4E60\u8BFE\u7A0B\u5728\u65AD\u65AD\u7EED\u7EED\u7684\u8282\u594F\u4E2D\uFF0C\u7EC8\u4E8E\u5B8C\u7ED3\u4E86\u3002\u4ECE\u5BF9\u673A\u5668\u5B66\u4E60\u53EA\u662F\u5355\u7EAF\u7684\u611F\u5174\u8DA3\u3001\u4E0D\u4E86\u89E3\uFF0C\u5230\u73B0\u5728\u5BF9\u673A\u5668\u5B66\u4E60\u5728\u8111\u4E2D\u6709\u4E86\u5927\u6982\u7684\u6982\u5FF5\u548C\u601D\u8DEF\uFF0C\u8FD9\u95E8\u8BFE\u7A0B\u5E2E\u52A9\u6211\u4E00\u8DEF\u8D70\u5230\u73B0\u5728\u3002</p><p>Andrew\u5927\u795E\u7528\u975E\u5E38\u6D45\u663E\u6613\u61C2\u7684\u65B9\u5F0F\u4F7F\u6211\u660E\u767D\u4E86\u5404\u79CD\u673A\u5668\u5B66\u4E60\u6A21\u578B\u7684\u5927\u6982\u601D\u8DEF\u548C\u7B80\u5355\u7684\u5B9E\u73B0\u65B9\u6CD5\uFF0C\u8FD9\u4E48\u6613\u61C2\u7684\u6559\u7A0B\u6211\u5728\u56FD\u5185\u4ECE\u672A\u53D1\u73B0\u8FC7\uFF0C\u73B0\u5728\u7684\u5404\u79CD\u6559\u7A0B\u57FA\u672C\u90FD\u662F\u4ECE\u6570\u5B66\u63A8\u7406\u548C\u5B9A\u4E49\u5165\u624B\uFF0C\u5404\u79CD\u516C\u5F0F\u5B9A\u7406\u63A8\u5BFC\u8BA9\u6211\u8FD9\u79CD\u4EBA\u770B\u4E86\u7B80\u76F4\u4E00\u8138\u61F5\u903C\uFF0C\u53EF\u80FD\u8FD8\u6CA1\u5F00\u59CB\u5165\u95E8\u5C31\u5DF2\u7ECF\u653E\u5F03\u4E86\u3002</p><p>\u8BFE\u7A0B\u5185\u5BB9\u57FA\u672C\u5206\u4E3A\u4E09\u90E8\u5206\uFF1A</p><ul><li>\u76D1\u7763\u5B66\u4E60</li><li>\u65E0\u76D1\u7763\u5B66\u4E60</li><li>\u7B97\u6CD5\u95EE\u9898\u6392\u67E5\u548C\u4F18\u5316\uFF08debug\uFF09</li></ul><p>\u5728<code>\u76D1\u7763\u5B66\u4E60</code>\u4E2D\uFF0C\u4ECB\u7ECD\u7684\u6A21\u578B\u548C\u7B97\u6CD5\u6709\uFF1A</p><ul><li>\u7EBF\u6027\u56DE\u5F52&amp;\u68AF\u5EA6\u4E0B\u964D&amp;\u6B63\u89C4\u65B9\u7A0B</li><li>\u903B\u8F91\u56DE\u5F52\uFF08\u4E8C\u5206\u7C7B&amp;\u591A\u5206\u7C7B\uFF09</li><li>\u795E\u7ECF\u7F51\u7EDC&amp;\u53CD\u5411\u4F20\u64AD</li><li>\u652F\u6301\u5411\u91CF\u673A&amp;\u6838\u51FD\u6570</li></ul><p>\u5728<code>\u65E0\u76D1\u7763\u5B66\u4E60</code>\u4E2D\uFF0C\u4ECB\u7ECD\u7684\u6A21\u578B\u548C\u7B97\u6CD5\u6709\uFF1A</p><ul><li>K-\u5747\u503C\u7B97\u6CD5&amp;\u4E3B\u6210\u5206\u5206\u6790\uFF08PCA\uFF09</li><li>\u5F02\u5E38\u68C0\u6D4B&amp;\u6B63\u6001\u5206\u5E03/\u8054\u5408\u6B63\u6001\u5206\u5E03</li><li>\u63A8\u8350\u7CFB\u7EDF&amp;\u534F\u540C\u8FC7\u6EE4\u7B97\u6CD5</li></ul><p>\u5728\u8BB2\u89E3\u8FD9\u4E9B\u5185\u5BB9\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u8BFE\u7A0B\u4E2D\u8FD8\u8BB2\u5230\u4E86\u5F88\u591A\u6709\u7528/\u5E38\u7528\u7684\u6280\u5DE7\u548C\u65B9\u6CD5\uFF1B</p><ul><li>\u4F7F\u7528\u6B63\u89C4\u5316\u9632\u6B62\u8FC7\u62DF\u5408\uFF08Regularization\uFF09</li><li>\u5728\u795E\u7ECF\u7F51\u7EDC\u548CK-\u5747\u503C\u7B97\u6CD5\u4E2D\u4F7F\u7528\u968F\u673A\u521D\u59CB\u5316\uFF08Random Initialization\uFF09</li><li>\u901A\u8FC7\u753B\u51FA\u5404\u79CD\u5173\u7CFB\u66F2\u7EBF\uFF08\u8FED\u4EE3\u6B21\u6570-\u4EE3\u4EF7\u51FD\u6570\u7B49\uFF09\u6765\u8BC4\u4F30\u6A21\u578B\u5F53\u524D\u7684\u6548\u679C\u548C\u5B58\u5728\u7684\u95EE\u9898</li><li>\u4F7F\u7528precision/recall\u8BC4\u4F30\u6A21\u578B\u7684\u8BAD\u7EC3\u6548\u679C</li><li>\u4F7F\u7528\u968F\u673A\u68AF\u5EA6\u4E0B\u964D/\u5C0F\u6279\u91CF\u68AF\u5EA6\u4E0B\u964D\u6765\u51CF\u5C11\u8BA1\u7B97\u91CF</li><li>\u4F7F\u7528map reduce\u7B49\u65B9\u6CD5\u4F7F\u5F97\u6A21\u578B\u80FD\u591F\u8FD0\u884C\u5728\u5206\u5E03\u5F0F\u67B6\u6784\u4E0A</li><li>\u5982\u4F55\u9009\u53D6\u7279\u5F81</li><li>\u5728\u7279\u5B9A\u60C5\u51B5\u4E0B\u5982\u4F55\u9009\u53D6\u5408\u9002\u7684\u7B97\u6CD5/\u6A21\u578B\uFF08\u6B63\u6001\u5206\u5E03\u7684\u4E58\u79EF vs \u8054\u5408\u6B63\u6001\u5206\u5E03\uFF09</li></ul><p>\u5728\u5B66\u4E60\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u628A\u903B\u8F91\u56DE\u5F52\u548C\u795E\u7ECF\u7F51\u7EDC\u7528Tensorflow\u81EA\u5DF1\u5B9E\u73B0\u4E86\u4E00\u904D\uFF0C\u8E29\u4E86\u53C8\u586B\u4E86\u4E00\u4E9B\u5751\uFF0C\u5BF9\u8FD9\u4E9B\u5185\u5BB9\u6709\u4E86\u66F4\u6DF1\u7684\u7406\u89E3\u3002\u5E0C\u671B\u81EA\u5DF1\u80FD\u591F\u7EE7\u7EED\u575A\u6301\u4E0B\u53BB\uFF0C\u5411\u7740\u771F\u6B63\u7684\u4EBA\u5DE5\u667A\u80FD\u524D\u8FDB\u3002</p>",11),p=[o];function r(s,c,_,d,n,u){return e(),i("div",null,p)}var T=l(t,[["render",r]]);export{f as __pageData,T as default};
