import{_ as o,c as s,o as t,a as r}from"./app.7001bd85.js";const u='{"title":"\u4E50\u7406\u57FA\u7840\u5B66\u4E60\u7B14\u8BB0\u2014\u2014\u4E94\u58F0\u8C03\u5F0F","description":"","frontmatter":{"title":"\u4E50\u7406\u57FA\u7840\u5B66\u4E60\u7B14\u8BB0\u2014\u2014\u4E94\u58F0\u8C03\u5F0F","date":"2017-06-11T11:31:22.000Z","tags":["music","music-theroy","notes"],"author":"dyzdyz010","collection":"study"},"headers":[],"relativePath":"posts/study/2017/music-theory-pentatonic-scale.md"}',n={},g=r("<p>\u4E94\u58F0\u8C03\u5F0F\u5373\u53EA\u6709\u4E94\u4E2A\u97F3\u4E0D\u65AD\u5FAA\u73AF\u7684\u8C03\u5F0F\u3002\u5982\u679C\u4ECEC\u5F00\u59CB\uFF0C\u4E94\u58F0\u8C03\u5F0F\u7684\u97F3\u9636\u4E3A\uFF1A</p><p><strong>C - D - E - G - A</strong></p><p>\u5373\u6211\u4EEC\u5E38\u8BF4\u7684<strong>\u5BAB\u5546\u89D2\u5FB5\u7FBD</strong>\uFF0C\u5531\u4F5C</p><p><strong>do - re - mi - sol - la</strong></p><p>\u662F\u4EC0\u4E48\u8C03\u5F0F\uFF0C\u54EA\u4E2A\u97F3\u5C31\u662F\u4E3B\u97F3\uFF1A</p><ul><li>C\u5BAB\u8C03\u5F0F\u7684\u4E3B\u97F3\u4E3A<strong>C</strong>\uFF0C\u5531\u540D\u4E3A<strong>do</strong>\uFF1B</li><li>D\u89D2\u8C03\u5F0F\u7684\u4E3B\u97F3\u4E3A<strong>D</strong>\uFF0C\u5531\u540D\u4E3A<strong>mi</strong>\uFF1B</li><li>G\u7FBD\u8C03\u5F0F\u7684\u4E3B\u97F3\u4E3A<strong>G</strong>\uFF0C\u5531\u540D\u4E3A<strong>la</strong>\uFF1B</li><li>\u2026\u2026</li></ul><p>\u4ECE\u97F3\u9636\u53EF\u4EE5\u770B\u51FA\uFF0C\u76F8\u6BD4\u6211\u4EEC\u719F\u77E5\u7684<strong>do re mi fa sol la si</strong>\uFF0C\u4E94\u58F0\u8C03\u5F0F\u7F3A\u5C11\u4E86<strong>IV\u7EA7</strong>\u548C<strong>VII\u7EA7</strong>\u97F3(\u5373<strong>fa</strong>\u548C<strong>si</strong>)\uFF0C\u7F3A\u5C11\u534A\u97F3\u548C\u589E\u56DB\u5EA6\u51CF\u4E94\u5EA6\uFF0C\u7531<strong>\u5927\u4E8C\u5EA6</strong>(do-re, sol-la, etc.)\u548C<strong>\u5C0F\u4E09\u5EA6</strong>(mi-sol, la-do)\u6784\u6210\u4E86\u57FA\u7840\u7684\u4E94\u58F0\u97F3\u8C03\u3002</p><p>\u5BAB\u5546\u89D2\u5FB4\u7FBD\u4E94\u79CD\u4E94\u58F0\u8C03\u5F0F\u5728\u4F5C\u66F2\u4E0A\u6709\u4E0D\u540C\u7684\u7F16\u6392\u548C\u7279\u70B9\uFF1A</p><p><strong>\u5BAB\u8C03\u5F0F</strong>\u7684\u7279\u5F81\u662F\u4E3B\u97F3\u4E0A\u65B9\u7684<strong>\u5927\u4E09\u5EA6</strong>(\u5373<strong>do-mi</strong>)\uFF0C\u5F3A\u8C03<strong>do-mi(mi-do)<strong>\u7684\u8FDB\u884C\uFF0C<strong>sol-la-do</strong>\u4E09\u4E2A\u97F3\u7ECF\u5E38</strong>\u6210\u7EC4\u51FA\u73B0</strong>\uFF0C\u4F46\u662F<strong>do-mi-sol</strong>\u4E09\u4E2A\u97F3\u7ECF\u5E38<strong>\u4E0D\u6210\u7EC4\u51FA\u73B0</strong>\uFF0C\u5373\u4E0D\u51FA\u73B0mi-do/sol-mi-do\u8FD9\u6837\u7684\u8FDB\u884C\uFF0C\u800C\u662F\u901A\u8FC7\u52A0\u5165<strong>re</strong>\u6216<strong>la</strong>\u6765\u8FC7\u6E21\uFF0C\u6700\u7EC8\u56DE\u5F52\u5230\u4E3B\u97F3do\uFF0C\u5982la-sol-mi-re-do\u3002</p><p><strong>\u5FB5\u8C03\u5F0F</strong>\u7684\u7279\u5F81\u662F\u4E3B\u97F3\u4E0A\u65B9\u7684\u7684<strong>\u7EAF\u56DB\u5EA6</strong>\u548C<strong>\u5927\u516D\u5EA6</strong>(\u5373<strong>so-do</strong>\uFF0C<strong>so-mi</strong>)\uFF0C\u5E38\u4EE5<strong>\u5BAB\u548C\u5F26</strong>(\u4E3B\u97F3\u4E3A\u5BAB\u97F3)\u5F00\u5934\uFF0C\u524D\u534A\u53E5\u901A\u5E38\u505C\u6B62\u5728re\uFF0C\u4F46\u6700\u540E\u8FD8\u662F\u4F1A\u505C\u6B62\u5728\u4E3B\u97F3sol\uFF1B\u5FB5\u8C03\u5F0F\u8FD8\u7ECF\u5E38\u51FA\u73B0<strong>do-la-sol</strong>\u7684\u4E0B\u884C\u8FDB\u884C\uFF0C\u540C\u65F6\u4E5F\u5E38\u5E38\u51FA\u73B0<strong>re-mi-la</strong>\u7684\u4E50\u53E5\u3002</p><p><strong>\u5546\u8C03\u5F0F</strong>\u7684\u7279\u5F81\u662F\u4E3B\u97F3\u4E0A\u65B9\u7684\u7684<strong>\u5927\u4E8C\u5EA6</strong>\u548C<strong>\u5C0F\u4E03\u5EA6</strong>(\u5373<strong>re-me</strong>\uFF0C<strong>re-do</strong>)\uFF0C\u8D77\u97F3\u548C\u5C3E\u97F3\u90FD\u662Fre\uFF0C<strong>re-mi-sol</strong>\u7684\u8FDB\u884C\u7ECF\u5E38\u51FA\u73B0\uFF0C\u7ED3\u5C3E\u901A\u5E38\u901A\u8FC7<strong>mi</strong>\u6216<strong>do</strong>\u89E3\u51B3\u5230\u6700\u7EC8\u7684\u5C3E\u97F3re\u3002</p><p><strong>\u7FBD\u8C03\u5F0F</strong>\u7684\u7279\u5F81\u662F\u4E3B\u97F3\u4E0A\u7684<strong>\u5C0F\u4E09\u5EA6</strong>\u548C<strong>\u7EAF\u4E94\u5EA6</strong>(\u5373<strong>la-do</strong>\uFF0C<strong>la-mi</strong>)\uFF0C\u540C\u6837\u662F\u4EE5\u4E3B\u97F3la\u5F00\u5934\uFF0C\u4EE5la\u7ED3\u5C3E\uFF0C\u5F3A\u8C03<strong>la-do-mi</strong>\u7684\u8FDB\u884C\uFF0C\u540C\u65F6\u4E5F\u4F1A\u7ECF\u5E38\u51FA\u73B0**mi-re-do(-la)**\u7684\u8FDB\u884C\u3002</p><p><strong>\u89D2\u8C03\u5F0F</strong>\u7684\u7279\u5F81\u662F\u4E3B\u97F3\u4E0A\u7684<strong>\u5C0F\u516D\u5EA6</strong>(\u5373<strong>mi-do</strong>)\uFF0C\u8D77\u4E8E\u5BAB\u548C\u5F26\uFF0C\u6B62\u4E8E\u89D2\u5927\u4E09\u548C\u5F26(<strong>mi-\u5347sol-si</strong>)\uFF1B\u5F3A\u8C03mi\u7684\u51FA\u73B0\uFF0C\u7ECF\u5E38\u505C\u6B62\u5728mi\u4E0A\uFF1B\u7ECF\u5E38\u51FA\u73B0<strong>la-sol-mi</strong>\u7684\u8FDB\u884C\u3002</p><p>\u4E94\u58F0\u8C03\u5F0F\u4E2D\uFF0C<strong>I\u7EA7</strong>\u97F3(\u4E3B\u97F3)\u662F<strong>\u6700\u7A33\u5B9A</strong>\u7684\uFF0C\u5728\u65CB\u5F8B\u4E2D\u5E94\u5F53\u5F3A\u8C03I\u7EA7\u97F3\uFF1B<strong>IV\u7EA7</strong>\u548C<strong>V\u7EA7</strong>\u97F3\u662F<strong>\u6B21\u7A33\u5B9A</strong>\u7684\uFF0C\u5BF9\u4E3B\u97F3\u6709\u652F\u6301\u7684\u4F5C\u7528\uFF1B\u5176\u4F59\u7684\u97F3\u7EA7\u662F\u4E0D\u7A33\u5B9A\u7684\uFF0C\u6709\u8FDB\u884C\u5230\u4EE5\u4E0A\u7A33\u5B9A\u97F3\u7EA7\u7684\u503E\u5411\uFF0C\u8FD9\u79CD\u7531\u4E0D\u7A33\u5B9A\u5230\u7A33\u5B9A\u7684\u8FDB\u884C\u8FC7\u7A0B\u6784\u6210\u4E86\u65CB\u5F8B\u7684\u96CF\u5F62\u3002</p>",14),e=[g];function i(l,a,d,p,m,c){return t(),s("div",null,e)}var f=o(n,[["render",i]]);export{u as __pageData,f as default};
