import{_ as t,c as e,o,a as s}from"./app.7001bd85.js";const u='{"title":"\u968F\u60F3\u2014\u2014\u5173\u4E8E\u533B\u7597","description":"","frontmatter":{"title":"\u968F\u60F3\u2014\u2014\u5173\u4E8E\u533B\u7597","date":"2022-05-20T08:56:03.000Z","author":"dyzdyz010","tags":["thoughts","medical"],"collection":"Thoughts","draft":false},"headers":[],"relativePath":"posts/thoughts/2022/20220520-thoughts-on-medical.md"}',p={},a=s("<p>\u6700\u8FD1\u51E0\u6B21\u53BB\u533B\u9662\uFF0C\u4E00\u6B21\u4F53\u68C0\uFF0C\u4E00\u6B21\u6025\u8BCA\uFF0C\u8BA9\u6211\u66F4\u575A\u5B9A\u4E86\u6211\u7684\u60F3\u6CD5\uFF0C\u90A3\u5C31\u662F\uFF0C\u533B\u7597\u4E0D\u662F\u6211\u4EEC\u65E5\u5E38\u751F\u6D3B\u4E2D\u7406\u6240\u5E94\u5F53\u80FD\u591F\u6307\u671B\u7684\u4E1C\u897F\uFF0C\u800C\u662F\u7C7B\u4F3C\u4E00\u79CD\u5962\u4F88\u54C1\u548C\u968F\u673A\u6027\u670D\u52A1\uFF0C\u4E0D\u80FD\u5728\u5927\u95EE\u9898\u4E0A\u6307\u671B\u3002</p><p>\u9996\u5148\u662F\u670D\u52A1\u6001\u5EA6\u3002\u6709\u4EBA\u53EF\u80FD\u4F1A\u8BF4\uFF0C\u533B\u9662\u53EA\u7BA1\u6CBB\u7597\uFF0C\u4ECE\u6765\u4E0D\u662F\u670D\u52A1\u884C\u4E1A\uFF0C\u65E0\u9700\u6CE8\u610F\u5BF9\u522B\u4EBA\u7684\u6001\u5EA6\u3002\u4F46\u662F\u6211\u7ECF\u5E38\u6709\u4E2A\u7591\u95EE\uFF0C\u65E2\u7136\u5B89\u6170\u5242\u6548\u5E94\u662F\u771F\u5B9E\u7684\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u7528\u5728\u533B\u60A3\u4EA4\u6D41\u4E2D\u5462\uFF1F\u533B\u9662\u548C\u533B\u751F\u7684\u76EE\u6807\u96BE\u9053\u4E0D\u662F\u8BA9\u60A3\u8005\u5EB7\u590D\u5417\uFF1F\u5982\u679C\u662F\u7684\u8BDD\uFF0C\u90A3\u96BE\u9053\u4E0D\u5E94\u8BE5\u628A\u6240\u6709\u80FD\u6709\u52A9\u4E8E\u8FD9\u4E2A\u76EE\u6807\u5B9E\u73B0\u7684\u65B9\u6CD5\u90FD\u5C3D\u53EF\u80FD\u7528\u4E0A\u5417\uFF1F\u7ECF\u5E38\u770B\u5230\u7F51\u4E0A\u8BF4\u533B\u751F\u5BF9\u764C\u75C7\u60A3\u8005\u8BF4\uFF0C\u8981\u6446\u6B63\u5FC3\u6001\u3001\u653E\u677E\u5FC3\u60C5\uFF0C\u8C03\u8282\u60C5\u7EEA\uFF0C\u6709\u52A9\u4E8E\u764C\u75C7\u6CBB\u7597\u3002\u90A3\u6211\u5C31\u60F3\u95EE\u4E86\uFF0C\u597D\u7684\u5FC3\u60C5\u548C\u5FC3\u6001\u5BF9\u5176\u4ED6\u75BE\u75C5\u662F\u5B8C\u5168\u6CA1\u5F71\u54CD\u5417\uFF1F\u53EA\u5BF9\u7EDD\u75C7\u60A3\u8005\u6709\u5F71\u54CD\uFF1F\u5982\u679C\u5BF9\u5176\u4ED6\u75C5\u75DB\u4E5F\u6709\u5F71\u54CD\u7684\u8BDD\uFF0C\u90A3\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u60F3\u529E\u6CD5\u8BA9\u60A3\u8005\u5728\u5BFB\u533B\u95EE\u8BCA\u7684\u8FC7\u7A0B\u4E2D\u4FDD\u6301\u4E00\u4E2A\u76F8\u5BF9\u5E73\u7A33\u7684\u5FC3\u60C5\u5462\uFF1F\u73B0\u5728\u53BB\u533B\u9662\u548C\u5DE5\u4F5C\u4EBA\u5458\u4EA4\u6D41\u7684\u8FC7\u7A0B\uFF0C\u6211\u7684\u611F\u89C9\u5C31\u662F\uFF0C\u4ED6/\u5979\u4E0D\u77E5\u53D7\u4E86\u591A\u5927\u7684\u6C14\u6216\u8005\u59D4\u5C48\uFF0C\u5728\u548C\u60A3\u8005\u4EA4\u6D41\u7684\u65F6\u5019\u90FD\u5728\u5C3D\u53EF\u80FD\u5730\u53D1\u6CC4\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF0C\u8F6C\u5934\u548C\u540C\u4E8B\u8BF4\u8BDD\u53C8\u662F\u53E6\u4E00\u5E45\u9762\u5B54\uFF0C\u8F7B\u677E\u6109\u5FEB\u3002\u8FD9\u79CD\u573A\u9762\u5728\u6211\u89C9\u5F97\u662F\u975E\u5E38\u96BE\u8FC7\uFF0C\u4E00\u79CD\u5C45\u9AD8\u4E34\u4E0B\u3001\u76DB\u6C14\u51CC\u4EBA\u7684\u611F\u89C9\u3002\u66F4\u4E0D\u7528\u63D0\u6709\u7684\u60A3\u8005\u53EF\u80FD\u4E00\u4E9B\u5730\u65B9\u6CA1\u6709\u4E86\u89E3\u6E05\u695A\uFF0C\u53BB\u8BE2\u95EE\u533B\u751F\u62A4\u58EB\u7684\u65F6\u5019\uFF0C\u90A3\u573A\u9762\uFF0C\u4EFF\u4F5B\u5C31\u548C\u8001\u5E08\u6559\u8BAD\u5C0F\u5B66\u751F\u4E00\u6837\u3002</p><p>\u8BED\u8A00\u4E2D\u7684\u60C5\u7EEA\u5728\u6211\u770B\u6765\u662F\u975E\u5E38\u6709\u4EF7\u503C\u7684\uFF0C\u53CB\u5584\u7684\u8BED\u6C14\u80AF\u5B9A\u6BD4\u51F6\u6076\u7684\u8BED\u6C14\u66F4\u80FD\u8BA9\u4EBA\u63A5\u53D7\u3002\u6709\u4EBA\u53C8\u53EF\u80FD\u4F1A\u8BF4\uFF0C\u6211\u8D39\u5FC3\u8D39\u529B\u597D\u58F0\u597D\u8BED\uFF0C\u53C8\u6CA1\u4EBA\u7ED9\u6211\u6DA8\u5DE5\u8D44\uFF0C\u6211\u4E0D\u662F\u767D\u767D\u4ED8\u51FA\u4E48\uFF1F\u4F46\u662F\u4ECE\u53E6\u4E00\u4E2A\u65B9\u9762\u8003\u8651\uFF0C\u9664\u4E86\u65E0\u8D56\uFF0C\u6709\u4E00\u90E8\u5206\u60A3\u8005\u60C5\u7EEA\u51B2\u52A8\uFF0C\u5BF9\u533B\u751F\u62A4\u58EB\u6076\u8BED\u76F8\u5411\u751A\u81F3\u201C\u533B\u95F9\u201D\uFF0C\u5728\u6211\u770B\u6765\u4E0E\u90E8\u5206\u533B\u751F\u62A4\u58EB\u7684\u4EA4\u6D41\u8BED\u6C14\u4E0D\u65E0\u5173\u7CFB\u3002\u6124\u6012\u3001\u4E0D\u8010\u70E6\u3001\u8F7B\u63CF\u6DE1\u5199\u7684\u8BED\u6C14\u5728\u6211\u770B\u6765\u65E0\u5F02\u4E8E\u6311\u8845\uFF0C\u7ED9\u60A3\u8005\u672C\u6765\u4F4E\u843D\u7684\u5FC3\u60C5\uFF0C\u6539\u5584\u5C31\u4E0D\u8BF4\u4E86\uFF0C\u81F3\u5C11\u662F\u80FD\u591F\u4F7F\u5176\u7A33\u5B9A\u90FD\u4E0D\u662F\uFF0C\u800C\u662F\u96EA\u4E0A\u52A0\u971C\u3002\u8FD9\u65E0\u7591\u589E\u52A0\u4E86\u60A3\u8005\u60C5\u7EEA\u7684\u4E0D\u7A33\u5B9A\u6027\uFF0C\u5982\u679C\u6709\u813E\u6C14\u6BD4\u8F83\u706B\u7206\u7684\u4EBA\uFF0C\u90A3\u6700\u540E\u5347\u7EA7\u6210\u8BED\u8A00\u80A2\u4F53\u51B2\u7A81\u90A3\u4E48\u4E5F\u5C31\u4E0D\u5947\u602A\u4E86\u3002</p><p>\u73B0\u5728\u793E\u4F1A\u4E00\u76F4\u8BA8\u4F10\u201C\u533B\u95F9\u201D\uFF0C\u6211\u4E5F\u53CD\u5BF9\u533B\u95F9\uFF0C\u4F46\u662F\u6211\u4EEC\u4E0D\u80FD\u5149\u558A\u53E3\u53F7\u800C\u4E0D\u53BB\u63A2\u7A76\u95EE\u9898\u7684\u6839\u672C\u539F\u56E0\u3002\u6211\u4EEC\u90FD\u77E5\u9053\u6253\u51FB\u72AF\u7F6A\u4E0D\u662F\u8FD9\u4E2A\u4EBA\u72AF\u7F6A\u4E86\u5BF9\u5176\u5B9E\u65BD\u60E9\u7F5A\u5C31\u5B8C\u4E8B\u4E86\uFF0C\u800C\u662F\u8981\u5206\u6790\u8FD9\u4E2A\u533A\u57DF\u72AF\u7F6A\u60C5\u51B5\u7684\u5B8F\u89C2\u6570\u636E\uFF0C\u4E3A\u4EC0\u4E48\u8FD9\u91CC\u72AF\u7F6A\u7387\u9AD8\uFF1F\u662F\u4E0D\u662F\u56E0\u4E3A\u7ECF\u6D4E\u53D1\u5C55\u6C34\u5E73\uFF1F\u7B49\u627E\u5230\u539F\u56E0\u540E\uFF0C\u4ECE\u539F\u56E0\u4E0B\u624B\u6539\u5584\uFF0C\u4ECE\u800C\u4F7F\u5F97\u72AF\u7F6A\u7387\u4E0B\u964D\u3002\u4F46\u662F\u5230\u4E86\u533B\u7597\u884C\u4E1A\uFF0C\u4E0D\u77E5\u4E3A\u4EC0\u4E48\u8FD9\u6837\u7684\u58F0\u97F3\u5C31\u5F88\u5C11\uFF0C\u90FD\u662F\u5404\u79CD\u53F7\u53EC\u533B\u7597\u884C\u4E1A\u5F88\u9AD8\u5C1A\u5F88\u8F9B\u82E6\uFF0C\u4EBA\u4EEC\u8981\u7406\u89E3\u4ED6\uFF0C\u51E1\u662F\u9020\u6210\u4F24\u5BB3\u7684\u90FD\u5E94\u8BE5\u8FFD\u7A76\u8D23\u4EFB\u3002\u7F51\u4E0A\u5F88\u5C11\u6709\u4EBA\u5206\u6790\u9020\u6210\u4ECA\u5929\u8FD9\u6837\u533B\u60A3\u77DB\u76FE\u7A81\u51FA\u3001\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\u7684\u6DF1\u5C42\u6B21\u539F\u56E0\u662F\u4EC0\u4E48\uFF0C\u90FD\u5728\u8F93\u51FA\u81EA\u5DF1\u7684\u523B\u677F\u5370\u8C61\uFF0C\u8BF4\u56E0\u4E3A\u8FD9\u4E2A\u4EBA\u90A3\u4E2A\u4EBA\u5C31\u662F\u574F\u3001\u5C31\u662F\u6545\u610F\uFF0C\u60A3\u8005\u5E94\u8BE5\u5929\u751F\u5C31\u61C2\u533B\u7597\u6D41\u7A0B\u3001\u624B\u7EED\u7684\u5168\u90E8\u3002\u4F46\u662F\u6211\u60F3\u8BF4\uFF0C\u8FFD\u7A76\u4E8B\u4EF6\u76F8\u5173\u4EBA\u7684\u8D23\u4EFB\u662F\u4E00\u65B9\u9762\uFF0C\u4F46\u662F\u9632\u60A3\u4E8E\u672A\u7136\u4E5F\u662F\u53E6\u4E00\u65B9\u9762\uFF0C\u4E0D\u80FD\u987E\u6B64\u5931\u5F7C\uFF0C\u51B5\u4E14\u9632\u60A3\u4E8E\u672A\u7136\u624D\u662F\u80FD\u591F\u4ECE\u6839\u672C\u4E0A\u89E3\u51B3\u533B\u60A3\u77DB\u76FE\u7684\u529E\u6CD5\u3002\u5728\u533B\u60A3\u4EA4\u6D41\u8FC7\u7A0B\u4E2D\uFF0C\u533B\u751F\u4E00\u8FB9\u81F3\u5C11\u80FD\u591F\u4FDD\u6301\u5FC3\u6001\u5E73\u548C\uFF0C\u8BED\u6C14\u6B63\u5E38\uFF0C\u4F7F\u5F97\u4EA4\u6D41\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u8D1F\u9762\u60C5\u7EEA\u5C3D\u53EF\u80FD\u5C11\uFF0C\u8FD9\u6837\u60A3\u8005\u7684\u5FC3\u60C5\u4E0D\u4F1A\u56E0\u4E3A\u8BED\u8A00\u800C\u8FDB\u4E00\u6B65\u53D8\u5DEE\uFF0C\u533B\u751F\u7FA4\u4F53\u964D\u4F4E\u4E86\u4E0E\u60A3\u8005\u8D77\u51B2\u7A81\u7684\u6982\u7387\uFF0C\u4E0D\u662F\u4E24\u5168\u5176\u7F8E\u4E48\uFF1F</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u597D\u7684\u8BED\u6C14\u4E5F\u53EF\u4EE5\u5BF9\u81EA\u5DF1\u7684\u5FC3\u6001\u548C\u5FC3\u60C5\u4EA7\u751F\u5F71\u54CD\u3002\u6240\u4EE5\u533B\u751F\u7FA4\u4F53\u5E38\u5E74\u5F85\u5728\u8D1F\u9762\u60C5\u7EEA\u7206\u68DA\u7684\u73AF\u5883\u4E2D\uFF0C\u5982\u679C\u80FD\u5BF9\u81EA\u5DF1\u7684\u60C5\u7EEA\u80FD\u6709\u6539\u5584\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u8BD5\u8BD5\u5462\uFF1F\u8BF4\u4E0D\u5B9A\u6BD4\u4E00\u5473\u8F93\u51FA\u60C5\u7EEA\u80FD\u4EA7\u751F\u66F4\u597D\u7684\u6548\u679C\u3002</p><p>\u53EF\u80FD\u56E0\u4E3A\u6211\u662F\u4E0D\u559C\u6B22\u51B2\u7A81\u7684\u4EBA\uFF0C\u5BF9\u8FD9\u79CD\u5251\u62D4\u5F29\u5F20\u7684\u6C1B\u56F4\u611F\u5230\u53CD\u611F\uFF0C\u6240\u4EE5\u6211\u4E00\u76F4\u90FD\u89C9\u5F97\u533B\u60A3\u5173\u7CFB\u5E94\u8BE5\u5411\u7740\u597D\u7684\u4E00\u9762\u53BB\u53D1\u5C55\uFF0C\u800C\u4E0D\u662F\u50CF\u73B0\u5728\u8FD9\u6837\u8D8A\u6765\u8D8A\u5272\u88C2\u3002\u5927\u5BB6\u90FD\u5E94\u8BE5\u671D\u7740\u66F4\u597D\u7684\u65B9\u5411\u53BB\u52AA\u529B\uFF0C\u8FD9\u6837\u624D\u80FD\u8BA9\u793E\u4F1A\u548C\u751F\u6D3B\u53D8\u5F97\u8D8A\u6765\u8D8A\u597D\u3002\u73B0\u5728\u8FD9\u79CD\u201C\u975E\u6211\u65CF\u7C7B\u5176\u5FC3\u5FC5\u5F02\u201D\u7684\u5FC3\u6001\u5728\u5404\u884C\u5404\u4E1A\u90FD\u5E7F\u6CDB\u5B58\u5728\uFF0C\u4E0D\u77E5\u662F\u4E0D\u662F\u8D8A\u6765\u8D8A\u591A\u7684\u4EBA\u5931\u53BB\u540C\u7406\u5FC3\u7684\u7ED3\u679C\u3002\u6211\u60F3\u4F46\u51E1\u4E00\u4E2A\u4EBA\u6709\u540C\u7406\u5FC3\uFF0C\u90FD\u4F1A\u6709\u201C\u5DF1\u6240\u4E0D\u6B32\u52FF\u65BD\u4E8E\u4EBA\u201D\u7684\u60F3\u6CD5\uFF0C\u4E0D\u5E94\u8BE5\u88AB\u884C\u4E1A\u628A\u81EA\u5DF1\u7684\u6027\u683C\u548C\u5904\u4E8B\u65B9\u5F0F\u5F02\u5316\u3002\u6211\u4EEC\u672C\u8BE5\u5BF9\u8EAB\u8FB9\u7684\u4EBA\u62E5\u6709\u60C5\u7EEA\u8FC1\u79FB\u7684\u80FD\u529B\uFF0C\u6709\u611F\u540C\u8EAB\u53D7\u7684\u80FD\u529B\uFF0C\u6709\u7406\u89E3\u5E76\u8868\u8FBE\u7684\u80FD\u529B\uFF0C\u4E0D\u4EC5\u662F\u533B\u60A3\u5173\u7CFB\uFF0C\u5BF9\u4EFB\u4F55\u53CC\u65B9\u5173\u7CFB\u90FD\u662F\u5982\u6B64\u3002\u4F5C\u4E3A\u6211\u4E2A\u4EBA\u7684\u4E00\u70B9\u79C1\u5FC3\uFF0C\u6211\u771F\u7684\u4E0D\u60F3\u6D3B\u5728\u4E00\u4E2A\u6240\u6709\u4EBA\u90FD\u51B7\u51B0\u51B0\uFF0C\u4E92\u76F8\u6212\u5907\u3001\u4E92\u76F8\u4E0D\u4FE1\u4EFB\u3001\u968F\u65F6\u51C6\u5907\u5BF9\u6297\u7684\u793E\u4F1A\u91CC\u3002</p><p>\u5176\u6B21\u662F\u533B\u7597\u884C\u4E1A\u6240\u63D0\u4F9B\u7684\u5185\u5BB9\uFF0C\u5373\u533B\u7597\u672C\u8EAB\u3002\u6211\u4E0D\u60F3\u628A\u5B83\u79F0\u4F5C <strong>\u533B\u7597\u670D\u52A1</strong> \uFF0C\u56E0\u4E3A\u6211\u89C9\u5F97\u533B\u7597\u548C\u670D\u52A1\u4E0D\u5728\u4E00\u4E2A\u5C42\u6B21\u4E0A\u3002\u5728\u6211\u7684\u60F3\u6CD5\u4E2D\uFF0C\u6211\u8BA4\u4E3A\u533B\u9662\u5C31\u4E0D\u8BE5\u662F\u7EAF\u7CB9\u7684\u81EA\u8D1F\u76C8\u4E8F\u673A\u6784\uFF0C\u6216\u8005\u8BF4\u76C8\u5229\u7684\u5185\u5BB9\u548C\u63D0\u4F9B\u7684\u533B\u7597\u5185\u5BB9\u5E94\u8BE5\u662F\u5206\u8D5B\u9053\u7684\u3002\u533B\u7597\u662F\u5BF9\u751F\u547D\u4FDD\u62A4\u7684\u6700\u540E\u624B\u6BB5\uFF0C\u62E5\u6709\u7279\u6B8A\u5730\u4F4D\uFF0C\u5982\u679C\u4EFB\u7531\u5176\u88AB\u76C8\u5229\u7684\u6B32\u671B\u5305\u88F9\uFF0C\u90A3\u73B0\u5728\u4E0E\u53E4\u4EE3\u7684\u751F\u547D\u6743\u5065\u5EB7\u6743\u5C06\u65E0\u751A\u5206\u522B\uFF0C\u90FD\u662F\u5F97\u4E0D\u5230\u4FDD\u969C\u7684\u7A7A\u4E2D\u697C\u9601\u3002\u5728\u6211\u7684\u60F3\u6CD5\u4E2D\uFF0C\u533B\u7597\u884C\u4E1A\u5C31\u5E94\u8BE5\u50CF\u5B66\u4E60\u519B\u961F\u7684\u62DB\u52DF\u7B56\u7565\uFF0C\u533B\u751F\u548C\u519B\u4EBA\u4E00\u6837\uFF0C\u4E00\u4E2A\u662F\u4FDD\u536B\u56FD\u5BB6\u5229\u76CA\uFF0C\u4E00\u4E2A\u662F\u4FDD\u969C\u4E2A\u4F53\u751F\u547D\u5065\u5EB7\uFF0C\u62E5\u6709\u7EDD\u5BF9\u7684\u8363\u8A89\u3002\u5BF9\u533B\u7597\u7CFB\u7EDF\u7684\u51C6\u5165\u4E5F\u5E94\u8BE5\u5B9E\u884C\u76F8\u5BF9\u4E25\u683C\u7684\u51C6\u5165\u6807\u51C6\uFF0C\u533B\u9662\u548C\u533B\u751F\u7FA4\u4F53\u6210\u4E3A\u53D7\u56FD\u5BB6\u4FDD\u62A4\u7684\u5355\u4F4D\u548C\u4EBA\u5458\uFF0C\u7B5B\u9009\u51FA\u4E00\u6279\u771F\u6B63\u60F3\u8981\u6551\u6B7B\u6276\u4F24\u7684\u4EBA\uFF0C\u800C\u4E0D\u662F\u50CF\u73B0\u5728\u4E00\u6837\u9C7C\u9F99\u6DF7\u6742\u3002\u533B\u7597\u8FD9\u4E48\u91CD\u8981\u7684\u884C\u4E1A\u4E0D\u5E94\u8BE5\u88AB\u6DF9\u6CA1\u5728\u5E02\u573A\u7ECF\u6D4E\u7684\u6C6A\u6D0B\u5927\u6D77\u91CC\u3002</p><p>\u4E0D\u8FC7\u8BDD\u8BF4\u56DE\u6765\uFF0C\u5176\u5B9E\u533B\u60A3\u7684\u4EA4\u6D41\u6C9F\u901A\u8FC7\u7A0B\u4E5F\u662F\u63D0\u4F9B\u533B\u7597\u5185\u5BB9\u7684\u4E00\u90E8\u5206\uFF0C\u6BD5\u7ADF\u8BED\u8A00\u2014\u2014\u60C5\u7EEA\u2014\u2014\u8EAB\u4F53\u5065\u5EB7\u7684\u94FE\u6761\u662F\u771F\u5B9E\u5B58\u5728\u7684\u3002\u5982\u679C\u90E8\u5206\u4EBA\u4E0D\u80FD\u628A\u8FD9\u79CD\u56E0\u7D20\u8003\u8651\u5728\u5185\uFF0C\u90A3\u6211\u662F\u4E0D\u662F\u53EF\u4EE5\u8BA4\u4E3A\u4ED6\u4EEC\u7684\u6700\u7EC8\u76EE\u6807\u5E76\u4E0D\u662F\u60A3\u8005\u7684\u5065\u5EB7\uFF1F\u4E0D\u628A\u60A3\u8005\u5065\u5EB7\u5F53\u505A\u6700\u7EC8\u76EE\u6807\u7684\u533B\u7597\u8FD8\u662F\u533B\u7597\u5417\uFF1F\u8FD8\u662F\u8BF4\u533B\u7597\u771F\u7684\u5C31\u5982\u540C\u5962\u4F88\u54C1\u4E00\u6837\uFF0C\u4E0D\u662F\u9762\u5411\u5927\u4F17\u7684\uFF1F</p><p>\u4EE5\u4E0A\u90FD\u662F\u6211\u53EF\u4EE5\u8BF4\u5F88\u4E0D\u5207\u5B9E\u9645\u7684\u60F3\u6CD5\uFF0C\u4F46\u662F\u5BF9\u793E\u4F1A\u53D8\u5F97\u8D8A\u6765\u8D8A\u597D\u7684\u671F\u671B\u662F\u771F\u7684\u3002\u5728\u6709\u6240\u6539\u53D8\u4E4B\u524D\uFF0C\u533B\u7597\u7684\u73B0\u72B6\u5C31\u662F\u5982\u6B64\u3002\u4E00\u65B9\u9762\u6211\u4EEC\u60F3\u8BA9\u533B\u7597\u53D8\u5F97\u66F4\u597D\uFF0C\u4F46\u53E6\u4E00\u65B9\u9762\u6211\u4EEC\u4E5F\u4E0D\u5E94\u8BE5\u5C06\u5E0C\u671B\u5B8C\u5168\u5BC4\u6258\u5728\u533B\u7597\u884C\u4E1A\u8EAB\u4E0A\uFF0C\u6211\u4EEC\u8981\u5BF9\u81EA\u5DF1\u7684\u5065\u5EB7\u8D1F\u8D23\uFF0C\u540C\u65F6\u675C\u7EDD\u865A\u5047\u7684\u5E0C\u671B\u3002\u5C3D\u4EBA\u4E8B\uFF0C\u542C\u5929\u547D\u624D\u662F\u5904\u4E16\u4E4B\u9053\uFF0C\u505A\u597D\u81EA\u5DF1\u80FD\u505A\u7684\uFF0C\u4E0D\u8BBA\u662F\u953B\u70BC\u8EAB\u4F53\u3001\u8FD8\u662F\u5065\u5EB7\u751F\u6D3B\uFF1B\u5269\u4E0B\u7684\u5C31\u4EA4\u7ED9\u547D\u8FD0\uFF0C\u800C\u4E0D\u662F\u4E0D\u5207\u5B9E\u9645\u5730\u5E7B\u60F3\u533B\u9662\u5C31\u80FD\u89E3\u51B3\u4F60\u7684\u4E00\u5207\u75C5\u75DB\u3002</p><p><strong>\u4E0D\uFF0C\u4ED6\u4EEC\u4E0D\u80FD\u3002</strong></p>",10),_=[a];function r(c,n,d,i,h,l){return o(),e("div",null,_)}var f=t(p,[["render",r]]);export{u as __pageData,f as default};
